import React, { useState } from 'react';

export default function DashboardPage({ onLogout }) {
  const [showAddPanel, setShowAddPanel] = useState(false);
  const [showPreviewPanel, setShowPreviewPanel] = useState(false);

  const handleAddProduct = () => {
    setShowPreviewPanel(true);
  };

  // Dati finti (simulazione) per ora
  const orders = [
    { id: 1, name: 'Red Velvet', quantity: 2, category: 'Cake' },
    { id: 2, name: 'Caprese', quantity: 1, category: 'Insalate' },
  ];

  return (
    <div className="p-6 max-w-md mx-auto bg-white min-h-screen">
      {/* Header */}
      <div className="bg-[#D39BFF] p-4 rounded-b-2xl text-white text-center mb-6 relative">
        <button onClick={onLogout} className="text-white text-sm underline absolute left-6 top-4">
          ← Logout
        </button>
        <h1 className="text-3xl font-bold">Dashboard</h1>
      </div>

      {/* Tasto + */}
      <div className="flex justify-end pr-4 mb-6">
        <button
          onClick={() => setShowAddPanel(prev => !prev)}
          className="text-2xl bg-[#D39BFF] text-white rounded-full w-12 h-12"
        >
          +
        </button>
      </div>

      {/* Pannello Aggiunta */}
  <>
      <AnimatePresence>
  {showAddPanel && (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: 20 }}
      transition={{ type: 'spring', stiffness: 300, damping: 20 }}
      className="bg-gray-100 p-4 rounded-2xl shadow mb-4"
    >
      <button
        className="bg-[#D39BFF] text-white px-4 py-2 rounded-xl w-full"
        onClick={handleAddProduct}
      >
        Inserisci Prodotto
      </button>
    </motion.div>
  )}
</AnimatePresence>
</>

      )}

      {/* Anteprima Aggiunta */}
      {showPreviewPanel && (
        <div className="border rounded-2xl shadow p-4 mb-6">
          <div className="w-full h-48 rounded-xl mb-2 overflow-hidden bg-gray-100 flex items-center justify-center">
            <img
              src="https://via.placeholder.com/300x200"
              alt="Anteprima"
              className="object-cover w-full h-full"
            />
          </div>
          <h3 className="text-lg font-semibold mb-2">Nome Prodotto</h3>
          <div className="flex justify-center gap-2 mb-2">
            <button className="bg-[#D39BFF] text-white px-4 py-1 rounded-xl">+2</button>
            <button className="bg-[#D39BFF] text-white px-4 py-1 rounded-xl">+5</button>
            <button className="bg-gray-400 text-white px-4 py-1 rounded-xl">+Altro</button>
          </div>
          <p className="text-sm text-gray-600">Totale: 0</p>
          <p className="text-sm text-gray-400">Disponibili: 0</p>
        </div>
      )}

      {/* Ordini Attivi */}
      <details open className="bg-white border rounded-2xl shadow p-4 mb-6">
        <summary className="flex items-center gap-2 cursor-pointer">
          <span className="h-6 w-6 bg-[#D39BFF] rounded-full"></span>
          <span className="text-lg font-semibold text-[#D39BFF]">Ordini Attivi</span>
        </summary>

        <ul className="divide-y divide-gray-200 mt-2">
          {orders.map(order => (
            <li key={order.id} className="py-2">
              <div className="flex justify-between items-center">
                <div>
                  <p className="font-semibold">{order.name}</p>
                  <p className="text-xs text-gray-400">Categoria: {order.category}</p>
                </div>
                <span className="font-bold text-[#D39BFF]">x{order.quantity}</span>
              </div>
            </li>
          ))}
        </ul>
      </details>

      {/* Ordini Totali (simulato) */}
      <details className="bg-white border rounded-2xl shadow p-4">
        <summary className="flex items-center gap-2 cursor-pointer">
          <span className="h-6 w-6 bg-gray-300 rounded-full"></span>
          <span className="text-lg font-semibold text-[#D39BFF]">Ordini Totali</span>
        </summary>

        <p className="text-sm text-gray-500 mt-2">Presto disponibile...</p>
      </details>
    </div>
  );
}
