import React, { useReducer, useMemo, lazy, Suspense, useState } from "react";

/* -------------------------------------------------------------
   UTILITY
-------------------------------------------------------------*/
function calculateTotal(orders) {
  return orders.reduce((sum, order) => sum + (order.total ?? order.price ?? 0), 0);
}

/* -------------------------------------------------------------
   REDUCER
-------------------------------------------------------------*/
function dashboardReducer(state, action) {
  switch (action.type) {
    case "ADD_ACTIVE_ORDER":
      return { ...state, activeOrders: [...state.activeOrders, action.order] };
    case "COMPLETE_ORDER":
      return {
        ...state,
        activeOrders: state.activeOrders.filter((o) => o.id !== action.id),
        completedOrders: [...state.completedOrders, action.order],
      };
    case "ADD_STOCK_ITEM":
      return { ...state, stockItems: [...state.stockItems, action.item] };
    case "TOGGLE_ORDER_MODAL":
      return { ...state, isOrderModalOpen: !state.isOrderModalOpen };
    default:
      return state;
  }
}

const initialState = {
  activeOrders: [],
  completedOrders: [],
  stockItems: [],
  isOrderModalOpen: false,
};

/* -------------------------------------------------------------
   MAIN COMPONENT – mobile‑first layout
-------------------------------------------------------------*/
const OrderModal = lazy(() => import("./OrderModal"));

export default function DashboardPage() {
  const [state, dispatch] = useReducer(dashboardReducer, initialState);

  const totalRevenue = useMemo(
    () => calculateTotal(state.completedOrders),
    [state.completedOrders]
  );

  return (
    <div className="min-h-screen flex flex-col gap-4 bg-gradient-to-b from-indigo-50 to-white">
      {/* Header */}
      <HeaderBar totalRevenue={totalRevenue} />

      <main className="flex-1 flex flex-col gap-4 px-4 pb-28 lg:pb-4 w-full lg:max-w-6xl lg:mx-auto">
        {/* 3‑col grid on large, 1‑col on mobile */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-4">
          <AddProductPanel
            onAddProduct={(product) =>
              dispatch({ type: "ADD_STOCK_ITEM", item: product })
            }
          />
          <OrdersActiveList
            orders={state.activeOrders}
            onComplete={(order) =>
              dispatch({ type: "COMPLETE_ORDER", id: order.id, order })
            }
          />
          <StockSection items={state.stockItems} />
        </div>

        <CompletedOrdersList orders={state.completedOrders} />
        <ChatBox />
      </main>

      {/* Floating action button per nuovo ordine */}
      <button
        className="fixed bottom-4 right-4 lg:hidden flex items-center gap-2 bg-emerald-600 hover:bg-emerald-700 text-white rounded-full shadow-lg px-6 py-3 text-lg font-semibold focus:outline-none"
        onClick={() => dispatch({ type: "TOGGLE_ORDER_MODAL" })}
      >
        + Ordine
      </button>

      {/* Modal */}
      <Suspense fallback={null}>
        {state.isOrderModalOpen && (
          <OrderModal
            onClose={() => dispatch({ type: "TOGGLE_ORDER_MODAL" })}
          />
        )}
      </Suspense>
    </div>
  );
}

/* -------------------------------------------------------------
   COMPONENTI FIGLI – palette & spacing ottimizzati
-------------------------------------------------------------*/
function Card({ children, className = "" }) {
  return (
    <section
      className={
        "bg-white/90 backdrop-blur rounded-2xl shadow-sm p-4 flex flex-col " +
        className
      }
    >
      {children}
    </section>
  );
}

function HeaderBar({ totalRevenue }) {
  return (
    <header className="sticky top-0 z-20 w-full bg-indigo-700 text-white px-4 py-3 flex items-center justify-between shadow-md">
      <h1 className="text-xl font-bold tracking-wide">Dashboard</h1>
      <span className="text-sm font-medium">
        Revenue Totale: €{totalRevenue.toFixed(2)}
      </span>
    </header>
  );
}

function AddProductPanel({ onAddProduct }) {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [qty, setQty] = useState(1);

  return (
    <Card>
      <h2 className="text-lg font-semibold mb-3">Aggiungi prodotto</h2>
      <input
        className="border border-slate-300 rounded-lg p-2 text-sm mb-2 focus:outline-none focus:ring-2 focus:ring-indigo-500"
        placeholder="Nome"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <input
        className="border border-slate-300 rounded-lg p-2 text-sm mb-2 focus:outline-none focus:ring-2 focus:ring-indigo-500"
        placeholder="Prezzo (€)"
        type="number"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
      />
      <input
        className="border border-slate-300 rounded-lg p-2 text-sm mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500"
        placeholder="Quantità"
        type="number"
        min={1}
        value={qty}
        onChange={(e) => setQty(parseInt(e.target.value, 10) || 1)}
      />
      <button
        className="w-full bg-indigo-600 hover:bg-indigo-700 active:scale-95 text-white rounded-lg py-2 text-sm font-medium transition"
        onClick={() => {
          if (!name || !price) return;
          onAddProduct({ id: Date.now(), name, qty, price: parseFloat(price) });
          setName("");
          setPrice("");
          setQty(1);
        }}
      >
        Salva
      </button>
    </Card>
  );
}

function OrdersActiveList({ orders, onComplete }) {
  return (
    <Card>
      <h2 className="text-lg font-semibold mb-3">Ordini Attivi</h2>
      <ul className="flex flex-col gap-2 overflow-y-auto max-h-72 pr-1">
        {orders.map((order) => (
          <li
            key={order.id}
            className="flex items-center justify-between bg-slate-100 rounded-lg px-3 py-2"
          >
            <span className="text-sm font-medium">{order.name}</span>
            <button
              className="bg-emerald-600 hover:bg-emerald-700 text-white rounded-md text-xs font-semibold px-2 py-1"
              onClick={() => onComplete(order)}
            >
              Completa
            </button>
          </li>
        ))}
        {orders.length === 0 && (
          <p className="text-slate-500 text-sm">Nessun ordine attivo.</p>
        )}
      </ul>
    </Card>
  );
}

function CompletedOrdersList({ orders }) {
  return (
    <Card>
      <h2 className="text-lg font-semibold mb-3">Ordini Completati</h2>
      <ul className="flex flex-col gap-2 overflow-y-auto max-h-72 pr-1">
        {orders.map((order) => (
          <li
            key={order.id}
            className="flex justify-between bg-slate-100 rounded-lg px-3 py-2"
          >
            <span className="text-sm font-medium">{order.name}</span>
            <span className="font-semibold text-sm">
              €{(order.total ?? order.price ?? 0).toFixed(2)}
            </span>
          </li>
        ))}
        {orders.length === 0 && (
          <p className="text-slate-500 text-sm">Nessun ordine completato.</p>
        )}
      </ul>
    </Card>
  );
}

function StockSection({ items }) {
  return (
    <Card>
      <h2 className="text-lg font-semibold mb-3">Magazzino</h2>
      <ul className="flex flex-col gap-2 overflow-y-auto max-h-72 pr-1">
        {items.map((item) => (
          <li
            key={item.id}
            className="flex justify-between bg-slate-50 rounded-lg px-3 py-2"
          >
            <span className="text-sm font-medium">{item.name}</span>
            <span className="font-semibold text-sm">{item.qty}</span>
          </li>
        ))}
        {items.length === 0 && (
          <p className="text-slate-500 text-sm">Magazzino vuoto.</p>
        )}
      </ul>
    </Card>
  );
}

function ChatBox() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  return (
    <Card className="mb-4 lg:mb-0">
      <h2 className="text-lg font-semibold mb-3">Chat</h2>
      <div className="flex flex-col gap-2 overflow-y-auto max-h-72 pr-1 mb-3">
        {messages.map((m, idx) => (
          <div key={idx} className="bg-slate-100 rounded-lg px-3 py-2 text-sm">
            {m}
          </div>
        ))}
        {messages.length === 0 && (
          <p className="text-slate-500 text-sm">Nessun messaggio.</p>
        )}
      </div>
      <div className="flex gap-2">
        <input
          className="flex-1 border border-slate-300 rounded-lg p-2 text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500"
          placeholder="Scrivi un messaggio…"
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button
          className="bg-indigo-600 hover:bg-indigo-700 text-white rounded-lg px-4 text-sm font-medium"
          onClick={() => {
            if (!input) return;
            setMessages([...messages, input]);
            setInput("");
          }}
        >
          Invia
        </button>
      </div>
    </Card>
  );
}
