import { useState } from 'react';
import productData from './data.json';
import logoFyfe from './assets/logo_fyfe.png';
import cakeIcon from './assets/torta_icon.png';
import saladIcon from './assets/salad_icon.png';
import specialsIcon from './assets/special_icon.png';
import { motion, AnimatePresence } from 'framer-motion';

const categories = {
  Cake: [
    { id: 1, name: 'Red Velvet', count: 0 },
    { id: 2, name: 'Torta al Limone', count: 0 },
  ],
  Insalate: [
    { id: 3, name: 'Greca', count: 0 },
    { id: 4, name: 'Caprese', count: 0 },
  ],
  Speciali: [
    { id: 5, name: 'Paccheri', count: 0 },
    { id: 6, name: 'Risotto Tartufato', count: 0 },
  ],
};

const categoryIcons = {
  Cake: <img src={cakeIcon} alt="Cake" className="w-10 h-10 object-contain" />, 
  Insalate: <img src={saladIcon} alt="Salad" className="w-10 h-10 object-contain" />, 
  Speciali: <img src={specialsIcon} alt="Specials" className="w-10 h-10 object-contain" />,
};

export default function FyfeApp() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showPasswordPrompt, setShowPasswordPrompt] = useState(false);
  const [passwordInput, setPasswordInput] = useState('');
  const [passwordError, setPasswordError] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [dishes, setDishes] = useState(categories);
  const [currentPage, setCurrentPage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isGoingToDashboard, setIsGoingToDashboard] = useState(false);

  if (!isAuthenticated) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-white">
        {!showPasswordPrompt ? (
          <button onClick={() => setShowPasswordPrompt(true)} className="focus:outline-none">
            <motion.img
              src={logoFyfe}
              alt="Fyfe Logo"
              className="w-32 h-32 object-contain"
              animate={{ scale: [1, 1.1, 1] }}
              transition={{ duration: 1.2, repeat: Infinity }}
            />
          </button>
        ) : (
          <div className="text-center">
            <h2 className="mb-4 text-xl font-semibold">U Are Real?👇</h2>
            <input
              type="password"
              className="border-2 border-[#D39BFF] px-4 py-2 rounded-2xl text-lg focus:ring-2 focus:ring-[#D39BFF] focus:outline-none transition-all duration-300"
              placeholder="Inserisci password..."
              value={passwordInput}
              onChange={e => setPasswordInput(e.target.value)}
            />
            {passwordError && <p className="text-red-500 mt-4">🤘 🤘 🤘 🤘 🤘 🤘</p>}
            <div className="mt-4">
              <button
                className="bg-[#D39BFF] text-white px-4 py-2 rounded-xl"
                onClick={() => {
                  if (passwordInput === 'fyfeadmin') {
                    setIsLoading(true);
                    setIsGoingToDashboard(true);
                    setTimeout(() => {
                      setIsAuthenticated(true);
                      setIsAdmin(true);
                      setPasswordError(false);
                    }, 600);
                  } else if (passwordInput === 'fyfe2025') {
                    setIsLoading(true);
                    setIsGoingToDashboard(false);
                    setTimeout(() => {
                      setIsAuthenticated(true);
                      setIsAdmin(false);
                      setPasswordError(false);
                    }, 600);
                  } else {
                    setPasswordError(true);
                  }
                }}
              >
                Entra
              </button>
            </div>
          </div>
        )}
      </div>
    );
  }

  return (
    <AnimatePresence mode="wait">
      {isLoading ? (
        <motion.div
          key="loading"
          initial={{ x: 0, y: 0 }}
          animate={
            isGoingToDashboard
              ? { y: [0, 500], transition: { type: "spring", bounce: 0.4, duration: 0.6 } }
              : { x: [0, 500], transition: { type: "spring", bounce: 0.4, duration: 0.6 } }
          }
          exit={{ opacity: 0 }}
          className="flex items-center justify-center h-screen bg-white"
        >
          <motion.img
            src={logoFyfe}
            alt="Fyfe Logo"
            className="w-32 h-32 object-contain"
            animate={{ scale: [1, 1.2, 1] }}
            transition={{ repeat: Infinity, duration: 1 }}
          />
        </motion.div>
      ) : isAdmin ? (
        <div className="p-4 max-w-md mx-auto bg-white min-h-screen overflow-hidden">
          <h2 className="text-2xl font-bold text-[#D39BFF] text-center mb-6">Dashboard Gestore</h2>
          {/* Qui dentro metti la dashboard che avevi */}
        </div>
      ) : (
        <div className="p-4 max-w-md mx-auto bg-white min-h-screen overflow-hidden">
          <AnimatePresence mode="wait">
            {!currentPage ? (
              <motion.div
                key="home"
                initial={{ x: -100, opacity: 0 }}
                animate={{ x: 0, opacity: 1 }}
                exit={{ x: 100, opacity: 0 }}
              >
                <div className="bg-[#D39BFF] p-4 rounded-b-2xl text-white text-center mb-4">
                  <h1 className="text-4xl font-bold" style={{ fontFamily: 'Monoton, sans-serif' }}>Fyfe 🍭</h1>
                </div>
                {/* Qui dentro metti la Home categorie che avevi */}
              </motion.div>
            ) : (
              <motion.div
                key="detail"
                initial={{ x: 100, opacity: 0 }}
                animate={{ x: 0, opacity: 1 }}
                exit={{ x: -100, opacity: 0 }}
              >
                {/* Qui dentro metti la pagina prodotti */}
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      )}
    </AnimatePresence>
  );
}
