import { useState } from 'react';
import logoFyfe from './assets/logo_fyfe.png';
import cakeIcon from './assets/torta_icon.png';
import saladIcon from './assets/salad_icon.png';
import specialsIcon from './assets/special_icon.png';
import { motion, AnimatePresence } from 'framer-motion';

// ---------- DATA ----------
const categories = {
  Cake: [
    { id: 1, name: 'Red Velvet', count: 0 },
    { id: 2, name: 'Torta al Limone', count: 0 },
  ],
  Insalate: [
    { id: 3, name: 'Greca', count: 0 },
    { id: 4, name: 'Caprese', count: 0 },
  ],
  Speciali: [
    { id: 5, name: 'Paccheri', count: 0 },
    { id: 6, name: 'Risotto Tartufato', count: 0 },
  ],
};

const categoryIcons = {
  Cake: <img src={cakeIcon} alt="Cake" className="w-10 h-10 object-contain" />,
  Insalate: <img src={saladIcon} alt="Salad" className="w-10 h-10 object-contain" />,
  Speciali: <img src={specialsIcon} alt="Specials" className="w-10 h-10 object-contain" />,
};

export default function FyfeApp() {
  // ---------- STATE ----------
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showPasswordPrompt, setShowPasswordPrompt] = useState(false);
  const [passwordInput, setPasswordInput] = useState('');
  const [passwordError, setPasswordError] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [dishes, setDishes] = useState(categories);
  const [currentPage, setCurrentPage] = useState(null);

  // ---------- HELPERS ----------
  const updateCount = (cat, id, qty) => {
    setDishes(prev => ({
      ...prev,
      [cat]: prev[cat].map(d => (d.id === id ? { ...d, count: d.count + qty } : d)),
    }));
  };

  const setCustomQty = (cat, id) => {
    const qty = parseInt(prompt('Quante porzioni vuoi aggiungere?'));
    if (!isNaN(qty)) updateCount(cat, id, qty);
  };

  // ---------- AUTH SCREENS ----------
  if (!isAuthenticated) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-white">
        {!showPasswordPrompt ? (
          <button onClick={() => setShowPasswordPrompt(true)} className="focus:outline-none">
            <motion.img
              src={logoFyfe}
              alt="Fyfe Logo"
              className="w-32 h-32 object-contain"
              animate={{ scale: [1, 1.1, 1] }}
              transition={{ duration: 1.2, repeat: Infinity }}
            />
          </button>
        ) : (
          <div className="text-center">
            <h2 className="mb-4 text-xl font-semibold">u are real?üëá</h2>
            <input
              type="password"
              className="border px-4 py-2 rounded-xl"
              value={passwordInput}
              onChange={e => setPasswordInput(e.target.value)}
            />
            {passwordError && <p className="text-red-500 mt-4">ü§ò ü§ò ü§ò ü§ò ü§ò ü§ò</p>}
            <div className="mt-4">
              <button
                className="bg-[#D39BFF] text-white px-4 py-2 rounded-xl"
                onClick={() => {
                  if (passwordInput === 'fyfeadmin') {
                    setIsAuthenticated(true);
                    setIsAdmin(true);
                    setPasswordError(false);
                  } else if (passwordInput === 'fyfe2025') {
                    setIsAuthenticated(true);
                    setIsAdmin(false);
                    setPasswordError(false);
                  } else {
                    setPasswordError(true);
                  }
                }}
              >
                Entra
              </button>
            </div>
          </div>
        )}
      </div>
    );
  }

  // ---------- ADMIN DASHBOARD ----------
  if (isAdmin) {
    const totalOrders = Object.entries(dishes)
      .flatMap(([cat, items]) => items.map(d => ({ ...d, category: cat })))
      .filter(d => d.count > 0);

    return (
      <div className="p-4 max-w-md mx-auto bg-white min-h-screen overflow-hidden">
        <h2 className="text-2xl font-bold text-[#D39BFF] text-center mb-6">Dashboard Gestore</h2>

        <div className="space-y-4">
          {/* ORDINI ATTIVI */}
          <details open className="bg-white border rounded-2xl shadow p-4">
            <summary className="flex items-center gap-2 cursor-pointer">
              <span className="h-6 w-6 bg-[#D39BFF] rounded-full"></span>
              <span className="text-lg font-semibold text-[#D39BFF]">Ordini Attivi</span>
            </summary>
            {totalOrders.length === 0 ? (
              <p className="text-gray-500 mt-2">Nessun ordine attivo</p>
            ) : (
              <ul className="divide-y divide-gray-200 mt-2">
                {totalOrders.map(item => (
                  <li key={item.id} className="py-2">
                    <div className="flex justify-between items-center">
                      <div>
                        <p className="font-semibold">{item.name}</p>
                        <p className="text-xs text-gray-400">Categoria: {item.category}</p>
                      </div>
                      <div className="flex items-center gap-2">
                        <span className="font-bold text-[#D39BFF]">x{item.count}</span>
                        <button
                          className="bg-red-500 text-white text-xs px-2 py-1 rounded-xl"
                          onClick={() => {
                            setDishes(prev => ({
                              ...prev,
                              [item.category]: prev[item.category].map(d =>
                                d.id === item.id ? { ...d, count: 0 } : d
                              ),
                            }));
                          }}
                        >
                          Completato
                        </button>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </details>

          {/* STORICO ORDINI */}
          <details className="bg-white border rounded-2xl shadow p-4">
            <summary className="flex items-center gap-2 cursor-pointer">
              <span className="h-6 w-6 bg-gray-300 rounded-full"></span>
              <span className="text-lg font-semibold text-[#D39BFF]">Storico Ordini</span>
            </summary>
            <p className="text-sm text-gray-500 mt-2">(In arrivo...)</p>
          </details>
        </div>

        <div className="text-center mt-6">
          <button
            className="bg-[#D39BFF] text-white px-6 py-2 rounded-2xl shadow"
            onClick={() => setIsAdmin(false)}
          >
            Torna alla Home
          </button>
        </div>
      </div>
    );
  }

  // ---------- USER SCREENS ----------
  return (
    <div className="p-4 max-w-md mx-auto bg-white min-h-screen overflow-hidden">
      <AnimatePresence mode="wait">
        {!currentPage ? (
          <motion.div
            key="home"
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: 100, opacity: 0 }}
          >
            <div className="bg-[#D39BFF] p-4 rounded-b-2xl text-white text-center mb-4">
              <div className="flex justify-between items-center">
                <button
                  onClick={() => setIsAuthenticated(false)}
                  className="text-white text-sm underline"
                >
                  ‚Üê Esci
                </button>
                <h1 className="text-4xl font-bold" style={{ fontFamily: 'Monoton, sans-serif' }}>
                  Fyfe
                </h1>
              </div>
            </div>

            {Object.entries(dishes).map(([category]) => (
              <div key={category} className="mb-4">
                <button
                  className="w-full text-left"
                  onClick={() => setCurrentPage(category)}
                >
                  <div className="flex items-center bg-white border rounded-2xl shadow p-4">
                    <div className="h-16 w-16 flex items-center justify-center rounded-full mr-4 bg-[#f3e8ff] border border-[#D39BFF]">
                      {categoryIcons[category]}
                    </div>
                    <h2 className="text-xl font-semibold text-[#D39BFF]">{category}</h2>
                  </div>
                </button>
              </div>
            ))}
          </motion.div>
        ) : (
          <motion.div
            key="detail"
            initial={{ x: 100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: -100, opacity: 0 }}
          >
            <div className="bg-[#D39BFF] p-4 rounded-b-2xl text-white text-center mb-4">
              <button className="text-white text-left" onClick={() => setCurrentPage(null)}>
                ‚Üê Torna Indietro
              </button>
              <div className="flex items-center justify-center gap-2 mt-2">
                <div className="text-2xl">{categoryIcons[currentPage]}</div>
                <h2 className="text-2xl font-bold capitalize">{currentPage}</h2>
              </div>
            </div>

            {dishes[currentPage].map(dish => (
  <div key={dish.id} className="border rounded-2xl shadow p-4 mb-4">
    <div className="w-full h-48 rounded-xl mb-2 overflow-hidden bg-gray-100 flex items-center justify-center">
      <img
        src="https://via.placeholder.com/300x200"
        alt="Placeholder"
        className="object-cover w-full h-full"
      />
    </div>

    <h3 className="text-lg font-semibold mb-2">{dish.name}</h3>
    <div className="flex justify-center gap-2 mb-2">
      <button
        className="bg-[#D39BFF] text-white px-4 py-1 rounded-xl"
        onClick={() => updateCount(currentPage, dish.id, 2)}
      >
        +2
      </button>
      <button
        className="bg-[#D39BFF] text-white px-4 py-1 rounded-xl"
        onClick={() => updateCount(currentPage, dish.id, 5)}
      >
        +5
      </button>
      <button
        className="bg-gray-400 text-white px-4 py-1 rounded-xl"
        onClick={() => setCustomQty(currentPage, dish.id)}
      >
        +Altro
      </button>
    </div>
    <p className="text-sm text-gray-600">Totale: {dish.count}</p>
  </div>
))}

</motion.div>
)}
</AnimatePresence>
</div>
);
}
