import React from 'react';

export default function HomePage({ onSelectCategory, onLogout }) {
  const categories = ['Cake', 'Insalate', 'Speciali'];

  return (
    <div className="p-6 max-w-md mx-auto bg-white min-h-screen">
      {/* Titolo */}
      <div className="bg-[#D39BFF] p-4 rounded-2xl text-white text-center mb-6">
        <h1 className="text-4xl font-bold">Fyfe üç≠</h1>
        <button
          onClick={onLogout}
          className="text-white text-sm underline mt-2"
        >
          Esci
        </button>
      </div>

      {/* Liste Categorie */}
      {categories.map((cat) => (
        <div key={cat} className="mb-4">
          <button
            onClick={() => onSelectCategory(cat)}
            className="w-full flex items-center justify-between p-4 border-2 rounded-2xl text-lg font-semibold text-[#D39BFF] hover:bg-[#f3e8ff] transition-all duration-300"
          >
            {cat}
          </button>
        </div>
      ))}
    </div>
  );
}
