import { useState } from 'react';
import cakeIcon from './assets/torta_icon.png';
import saladIcon from './assets/salad_icon.png';
import specialsIcon from './assets/special_icon.png';

import { motion, AnimatePresence } from 'framer-motion';

const categories = {
  Cake: [
    { id: 1, name: 'Red Velvet', count: 0 },
    { id: 2, name: 'Torta al Limone', count: 0 },
  ],
  Insalate: [
    { id: 3, name: 'Greca', count: 0 },
    { id: 4, name: 'Caprese', count: 0 },
  ],
  Speciali: [
    { id: 5, name: 'Paccheri', count: 0 },
    { id: 6, name: 'Risotto Tartufato', count: 0 },
  ]
};

const categoryIcons = {
  Cake: <img src={cakeIcon} alt="Cake" className="w-10 h-10" />,
  Insalate: <img src={saladIcon} alt="Salad" className="w-10 h-10" />,
  Speciali: <img src={specialsIcon} alt="Specials" className="w-10 h-10" />
};

export default function FyfeApp() {
  const [dishes, setDishes] = useState(categories);
  const [currentPage, setCurrentPage] = useState(null);

  const updateCount = (cat, id, qty) => {
    setDishes(prev => ({
      ...prev,
      [cat]: prev[cat].map(d => d.id === id ? { ...d, count: d.count + qty } : d)
    }));
  };

  const setCustomQty = (cat, id) => {
    const input = prompt('Quante porzioni vuoi aggiungere?');
    const qty = parseInt(input);
    if (!isNaN(qty)) updateCount(cat, id, qty);
  };

  const resetCounts = () => {
    const reset = {};
    for (const cat in dishes) {
      reset[cat] = dishes[cat].map(d => ({ ...d, count: 0 }));
    }
    setDishes(reset);
  };

  return (
    <div className="p-4 max-w-md mx-auto bg-white min-h-screen overflow-hidden">
      <AnimatePresence mode="wait">
        {!currentPage ? (
          <motion.div
            key="home"
            initial={{ x: -100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: 100, opacity: 0 }}
          >
            <div className="bg-[#D39BFF] p-4 rounded-b-2xl text-white text-center mb-4">
              <h1 className="text-4xl font-bold">Fyfe</h1>
            </div>

            {Object.entries(dishes).map(([category]) => (
              <div key={category} className="mb-4">
                <button onClick={() => setCurrentPage(category)} className="w-full text-left">
                  <div className="flex items-center bg-white border rounded-2xl shadow p-4">
                    <div className="h-16 w-16 flex items-center justify-center text-3xl rounded-full mr-4 bg-[#f3e8ff] border border-[#D39BFF]">
                      {categoryIcons[category]}
                    </div>
                    <div>
                      <h2 className="text-xl font-semibold text-[#D39BFF]">{category}</h2>
                    </div>
                  </div>
                </button>
              </div>
            ))}

            <div className="text-center">
              <button onClick={resetCounts} className="mt-6 bg-red-500 text-white px-6 py-2 rounded-2xl shadow">Reset Totali</button>
            </div>
          </motion.div>
        ) : (
          <motion.div
            key="detail"
            initial={{ x: 100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: -100, opacity: 0 }}
          >
            <div className="bg-[#D39BFF] p-4 rounded-b-2xl text-white text-center mb-4">
              <button onClick={() => setCurrentPage(null)} className="text-white text-left">‚Üê Torna Indietro</button>
              <div className="flex items-center justify-center gap-2">
                <div className="text-2xl">{categoryIcons[currentPage]}</div>
                <h2 className="text-2xl font-bold">{currentPage}</h2>
              </div>
            </div>
            {dishes[currentPage].map(dish => (
              <div key={dish.id} className="border rounded-2xl shadow p-4 mb-4">
                <div className="w-full h-48 rounded-xl mb-2 overflow-hidden">
                  <div className="flex overflow-x-auto space-x-2 snap-x snap-mandatory scrollbar-hide">
                    <div className="snap-start w-full h-48 flex-shrink-0 rounded-xl bg-gray-100 flex items-center justify-center">
                      <img src="https://via.placeholder.com/300x200" alt="Dish" className="object-cover w-full h-full" />
                    </div>
                    <div className="snap-start w-full h-48 flex-shrink-0 rounded-xl bg-gray-100 flex items-center justify-center">
                      <video className="w-full h-full object-cover" controls>
                        <source src="https://www.w3schools.com/html/mov_bbb.mp4" type="video/mp4" />
                      </video>
                    </div>
                  </div>
                  <div className="flex justify-center gap-1 mt-1">
                    <div className="w-2 h-2 bg-[#D39BFF] rounded-full"></div>
                    <div className="w-2 h-2 bg-gray-300 rounded-full"></div>
                  </div>
                </div>
                <h3 className="text-lg font-semibold mb-2">{dish.name}</h3>
                <div className="flex justify-center gap-2 mb-2">
                  <button onClick={() => updateCount(currentPage, dish.id, 2)} className="bg-[#D39BFF] text-white px-4 py-1 rounded-xl">+2</button>
                  <button onClick={() => updateCount(currentPage, dish.id, 5)} className="bg-[#D39BFF] text-white px-4 py-1 rounded-xl">+5</button>
                  <button onClick={() => setCustomQty(currentPage, dish.id)} className="bg-gray-400 text-white px-4 py-1 rounded-xl">+Altro</button>
                </div>
                <div className="text-sm text-gray-600">Totale: {dish.count}</div>
              </div>
            ))}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
