import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

export default function DashboardPage({ onLogout }) {
  const [showAddPanel, setShowAddPanel] = useState(false);
  const [showPreviewPanel, setShowPreviewPanel] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [openOrderModal, setOpenOrderModal] = useState(null);
  const [activeOrders, setActiveOrders] = useState([
    { id: 1, name: 'Red Velvet', quantity: 2, category: 'Cake', price: 5, details: 'Con crema di formaggio, 2 strati, 500g.', address: 'Via Roma 123, Milano' },
    { id: 2, name: 'Caprese', quantity: 1, category: 'Insalate', price: 8, details: 'Pomodoro, mozzarella, basilico fresco.', address: 'Corso Italia 45, Napoli' },
  ]);
  const [completedOrders, setCompletedOrders] = useState([]);

  const calculateTotal = (order) => {
    return order.price * order.quantity;
  };

  const handleCloseOrder = (order) => {
    setCompletedOrders([...completedOrders, order]);
    setActiveOrders(activeOrders.filter(o => o.id !== order.id));
    setOpenOrderModal(null);
  };

  return (
    <div className="p-6 max-w-md mx-auto bg-white min-h-screen relative">

      {/* Header */}
      <div className="bg-[#D39BFF] p-4 rounded-b-2xl text-white text-center mb-6 relative">
        <button
          onClick={onLogout}
          className="text-white text-sm underline absolute left-6 top-4"
        >
          ← Logout
        </button>
        <h1 className="text-3xl font-bold">Dashboard</h1>
      </div>

      {/* Tasto + */}
      <div className="flex justify-end pr-4 mb-6">
        <motion.button
          onClick={() => setShowAddPanel(prev => !prev)}
          className="text-2xl bg-[#D39BFF] text-white rounded-full w-12 h-12 flex items-center justify-center"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
        >
          +
        </motion.button>
      </div>

      {/* Pannello Aggiunta Prodotto */}
      <AnimatePresence>
        {showAddPanel && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            transition={{ type: 'spring', stiffness: 300, damping: 20 }}
            className="bg-gray-100 p-4 rounded-2xl shadow mb-6"
          >
            <button
              className="bg-[#D39BFF] text-white px-4 py-2 rounded-xl w-full"
              onClick={() => setShowPreviewPanel(true)}
            >
              Inserisci Prodotto
            </button>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Anteprima Prodotto */}
      <AnimatePresence>
        {showPreviewPanel && (
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.95 }}
            transition={{ duration: 0.4, ease: 'easeInOut' }}
            className="border rounded-2xl shadow p-4 mb-6"
          >
            <div className="w-full h-48 rounded-xl mb-2 overflow-hidden bg-gray-100 flex items-center justify-center">
              <img
                src="https://via.placeholder.com/300x200"
                alt="Anteprima"
                className="object-cover w-full h-full"
              />
            </div>
            <h3 className="text-lg font-semibold mb-2">Nome Prodotto</h3>
            <div className="flex justify-center gap-2 mb-2">
              <button className="bg-[#D39BFF] text-white px-4 py-1 rounded-xl">+2</button>
              <button className="bg-[#D39BFF] text-white px-4 py-1 rounded-xl">+5</button>
              <button className="bg-gray-400 text-white px-4 py-1 rounded-xl">+Altro</button>
            </div>
            <p className="text-sm text-gray-600">Totale: 0</p>
            <p className="text-sm text-gray-400">Disponibili: 0</p>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Ordini Attivi */}
      <details open className="bg-white border rounded-2xl shadow p-4 mb-6">
        <summary className="flex items-center gap-2 cursor-pointer">
          <span className="h-6 w-6 bg-[#D39BFF] rounded-full"></span>
          <span className="text-lg font-semibold text-[#D39BFF]">Ordini Attivi</span>
        </summary>

        <ul className="divide-y divide-gray-200 mt-2">
          {activeOrders.map(order => (
            <li key={order.id} className="py-2">
              <div
                onClick={() => setOpenOrderModal(order)}
                className="flex justify-between items-center cursor-pointer"
              >
                <div>
                  <p className="font-semibold">{order.name}</p>
                  <p className="text-xs text-gray-400">Categoria: {order.category}</p>
                  <p className="text-xs text-gray-400">Indirizzo: {order.address}</p>
                </div>
                <div className="flex flex-col items-end">
                  <span className="font-bold text-[#D39BFF]">x{order.quantity}</span>
                  <span className="text-sm text-gray-500">€{calculateTotal(order)}</span>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </details>

      {/* Ordini Totali */}
      <details className="bg-white border rounded-2xl shadow p-4">
        <summary className="flex items-center gap-2 cursor-pointer">
          <span className="h-6 w-6 bg-gray-300 rounded-full"></span>
          <span className="text-lg font-semibold text-[#D39BFF]">Ordini Totali</span>
        </summary>

        <ul className="divide-y divide-gray-200 mt-2">
          {completedOrders.map(order => (
            <li key={order.id} className="py-2">
              <div className="flex justify-between items-center">
                <div>
                  <p className="font-semibold">{order.name}</p>
                  <p className="text-xs text-gray-400">Categoria: {order.category}</p>
                  <p className="text-xs text-gray-400">Indirizzo: {order.address}</p>
                </div>
                <div className="flex flex-col items-end">
                  <span className="font-bold text-green-500">x{order.quantity}</span>
                  <span className="text-sm text-gray-500">€{calculateTotal(order)}</span>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </details>

      {/* Modal Info Cliente o Ordine */}
      <AnimatePresence>
        {openOrderModal && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50 p-4"
          >
            <motion.div
              initial={{ scale: 0.8 }}
              animate={{ scale: 1 }}
              exit={{ scale: 0.8 }}
              transition={{ type: 'spring', stiffness: 300, damping: 20 }}
              className="bg-white p-6 rounded-2xl max-w-md w-full max-h-[95vh] overflow-y-auto relative"
            >
              <button
                onClick={() => setOpenOrderModal(null)}
                className="absolute top-4 right-4 text-gray-400 hover:text-gray-600"
              >
                ✕
              </button>
              <div className="mb-6">
                <div className="flex justify-between items-center">
                  <h2 className="text-3xl font-bold text-[#D39BFF]">Dettagli Ordine</h2>
                </div>
                <div className="flex justify-end mt-4">
                  <button
                    onClick={() => handleCloseOrder(openOrderModal)}
                    className="bg-[#D39BFF] text-white px-4 py-2 rounded-3xl text-sm"
                  >
                    Closed
                  </button>
                </div>
              </div>
              <div className="space-y-6">
                <div>
                  <h3 className="text-lg font-semibold text-[#D39BFF] mb-2">Info Ordine</h3>
                  <p className="text-sm"><span className="font-semibold">Nome:</span> {openOrderModal.name}</p>
                  <p className="text-sm"><span className="font-semibold">Categoria:</span> {openOrderModal.category}</p>
                  <p className="text-sm"><span className="font-semibold">Quantità:</span> {openOrderModal.quantity}</p>
                  <p className="text-sm"><span className="font-semibold">Prezzo unitario:</span> €{openOrderModal.price}</p>
                  <p className="text-sm"><span className="font-semibold">Totale Ordine:</span> €{calculateTotal(openOrderModal)}</p>
                </div>
                <hr className="border-gray-200" />
                <div>
                  <h3 className="text-lg font-semibold text-[#D39BFF] mb-2">Info Cliente</h3>
                  <p className="text-sm"><span className="font-semibold">Descrizione:</span> {openOrderModal.details}</p>
                </div>
                <hr className="border-gray-200" />
                <div>
                  <h3 className="text-lg font-semibold text-[#D39BFF] mb-2">Consegna</h3>
                  <p className="text-sm"><span className="font-semibold">Indirizzo:</span> {openOrderModal.address}</p>
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
