import React, { useState, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

export default function DashboardPage({ onLogout }) {
  // Stati principali
  const [showPreviewPanel, setShowPreviewPanel] = useState(false);
  const [openOrderModal, setOpenOrderModal] = useState(null);

  // Ordini
  const [activeOrders, setActiveOrders] = useState([
    {
      id: 1,
      name: 'Red Velvet',
      quantity: 2,
      category: 'Cake',
      price: 5,
      details: 'Con crema di formaggio, 2 strati, 500g.',
      nick: 'Cliente1',
      message: 'Grazie!',
      address: 'Via Roma 123, Milano'
    },
    {
      id: 2,
      name: 'Caprese',
      quantity: 5,
      category: 'Insalate',
      price: 8,
      details: 'Pomodoro, mozzarella, basilico fresco.',
      nick: 'Cliente2',
      message: 'Buon appetito',
      address: 'Corso Italia 45, Napoli'
    }
  ]);
  const [completedOrders, setCompletedOrders] = useState([]);

  // Calcolo incasso totale
  const calculateTotal = (order) => order.price * order.quantity;
  const totalRevenue = completedOrders.reduce((sum, order) => sum + calculateTotal(order), 0);

  // Prodotti in vendita (menù)
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', price: '', quantity: '', image: null, imageUrl: '' });

  // Stock di magazzino
  const [stockItems, setStockItems] = useState([]);
  const [newStock, setNewStock] = useState({ name: '', stock2: '', stock5: '' });

  const hiddenFileInput = useRef(null);

  // Gestione chiusura ordine
  const handleCloseOrder = (order) => {
    setCompletedOrders((prev) => [...prev, order]);
    setActiveOrders((prev) => prev.filter((o) => o.id !== order.id));

    // Aggiorna stock
    setStockItems((prev) =>
      prev.map((item) => {
        if (item.name === order.name) {
          const updated = { ...item };
          if (order.quantity === 2) updated.stock2 = Math.max(0, updated.stock2 - 1);
          if (order.quantity === 5) updated.stock5 = Math.max(0, updated.stock5 - 1);
          return updated;
        }
        return item;
      })
    );

    // Aggiorna prodotti menù
    setProducts((prev) =>
      prev.map((prod) =>
        prod.name === order.name
          ? { ...prod, quantity: Math.max(0, prod.quantity - order.quantity) }
          : prod
      )
    );

    setOpenOrderModal(null);
  };

  // Gestione immagine
  const handleImageClick = () => hiddenFileInput.current.click();
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) setNewProduct((p) => ({ ...p, image: file, imageUrl: URL.createObjectURL(file) }));
  };

  // Aggiunge nuovo prodotto al menù
  const handleAddProduct = () => {
    const { name, price, quantity } = newProduct;
    if (name && price && quantity) {
      setProducts((prev) => [...prev, newProduct]);
      setNewProduct({ name: '', price: '', quantity: '', image: null, imageUrl: '' });
      setShowPreviewPanel(false);
    } else {
      alert('Compila tutti i campi per aggiungere il prodotto.');
    }
  };

  // Aggiunge o aggiorna stock di magazzino
  const handleAddStock = () => {
    const { name, stock2, stock5 } = newStock;
    if (name && stock2 && stock5) {
      setStockItems((prev) => {
        const exists = prev.find((item) => item.name === name);
        if (exists) {
          return prev.map((item) =>
            item.name === name
              ? { name, stock2: parseInt(stock2, 10), stock5: parseInt(stock5, 10) }
              : item
          );
        }
        return [...prev, { name, stock2: parseInt(stock2, 10), stock5: parseInt(stock5, 10) }];
      });
      setNewStock({ name: '', stock2: '', stock5: '' });
    } else {
      alert('Compila tutti i campi per aggiornare lo stock.');
    }
  };

  return (
    <div className="p-6 max-w-md mx-auto bg-white min-h-screen relative">
      {/* Header */}
      <div className="bg-[#D39BFF] p-4 rounded-b-2xl text-white text-center mb-6 relative">
        <button onClick={onLogout} className="text-white text-sm underline absolute left-6 top-4">← Logout</button>
        <h1 className="text-3xl font-bold">Dashboard</h1>
      </div>

      {/* Tasto + Aggiungi Prodotto */}
      <div className="flex justify-end pr-4 mb-6">
        <motion.button
          onClick={() => setShowPreviewPanel((prev) => !prev)}
          className="text-2xl bg-[#D39BFF] text-white rounded-full w-12 h-12 flex items-center justify-center"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
        >+
        </motion.button>
      </div>

      {/* Popup Aggiungi Prodotto */}
      <AnimatePresence>
        {showPreviewPanel && (
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.95 }}
            transition={{ duration: 0.3 }}
            className="border rounded-2xl shadow p-4 mb-6"
          >
            <div
              className="w-full h-48 rounded-xl mb-2 bg-gray-100 flex items-center justify-center cursor-pointer"
              onClick={handleImageClick}
            >
              {newProduct.imageUrl ? (
                <img src={newProduct.imageUrl} alt="Anteprima" className="object-cover w-full h-full" />
              ) : (
                <span className="text-gray-400">Clicca per caricare immagine</span>
              )}
              <input type="file" accept="image/*" className="hidden" ref={hiddenFileInput} onChange={handleImageUpload} />
            </div>
            <input type="text" placeholder="Nome Prodotto" className="w-full border p-2 rounded-xl mb-2" value={newProduct.name} onChange={(e) => setNewProduct((p) => ({ ...p, name: e.target.value }))} />
            <input type="number" placeholder="Prezzo" className="w-full border p-2 rounded-xl mb-2" value={newProduct.price} onChange={(e) => setNewProduct((p) => ({ ...p, price: e.target.value }))} />
            <input type="text" placeholder="Categoria" className="w-full border p-2 rounded-xl mb-2" value={newProduct.category || ''} onChange={(e) => setNewProduct((p) => ({ ...p, category: e.target.value }))} />
            <input type="number" placeholder="Disponibilità" className="w-full border p-2 rounded-xl mb-4" value={newProduct.quantity} onChange={(e) => setNewProduct((p) => ({ ...p, quantity: e.target.value }))} />
            <button onClick={handleAddProduct} className="w-full bg-[#D39BFF] text-white py-2 rounded-xl font-semibold" className="w-full bg-[#D39BFF] text-white py-2 rounded-xl font-semibold">Inserisci Prodotto</button>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Sezione Ordini Attivi */}
      <details open className="bg-white border rounded-2xl shadow p-4 mb-6">
        <summary className="flex items-center gap-2 cursor-pointer">
          <span className="h-6 w-6 bg-[#D39BFF] rounded-full" />
          <span className="text-lg font-semibold text-[#D39BFF]">Ordini Attivi</span>
        </summary>
        <ul className="divide-y divide-gray-200 mt-2">
          {activeOrders.map((order) => (
            <li key={order.id} className="py-2">
              <div onClick={() => setOpenOrderModal(order)} className="flex justify-between items-center cursor-pointer">
                <div>
                  <p className="font-semibold">{order.name}</p>
                  <p className="text-xs text-gray-400">Categoria: {order.category}</p>
                  <p className="text-xs text-gray-400">Address: {order.address}</p>
                </div>
                <div className="flex flex-col items-end">
                  <span className="font-bold text-[#D39BFF]">x{order.quantity}</span>
                  <span className="text-sm text-gray-500">€{calculateTotal(order)}</span>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </details>

      {/* Sezione Ordini Totali */}
      <details className="bg-white border rounded-2xl shadow p-4 mb-6">
        <summary className="flex items-center gap-2 cursor-pointer">
          <span className="h-6 w-6 bg-gray-300 rounded-full" />
          <span className="text-lg font-semibold text-[#D39BFF]">Ordini Totali</span>
          <span className="ml-auto text-sm text-[#D39BFF]">Incasso: €{totalRevenue}</span>
        </summary>
        <ul className="divide-y divide-gray-200 mt-2">
          {completedOrders.map((order) => (
            <li key={order.id} className="py-2">
              <div className="flex justify-between items-center">
                <div>
                  <p className="font-semibold">{order.name}</p>
                  <p className="text-xs text-gray-400">Categoria: {order.category}</p>
                  <p className="text-xs text-gray-400">Address: {order.address}</p>
                </div>
                <div className="flex flex-col items-end">
                  <span className="font-bold text-green-500">x{order.quantity}</span>
                  <span className="text-sm text-gray-500">€{calculateTotal(order)}</span>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </details>

      {/* Sezione Stock Prodotti */}
      <details className="bg-white border rounded-2xl shadow p-4 mb-6">
        <summary className="flex items-center gap-2 cursor-pointer">
          <span className="h-6 w-6 bg-[#D39BFF] rounded-full" />
          <span className="text-lg font-semibold text-[#D39BFF]">Stock Prodotti</span>
        </summary>
        <div className="mt-4 mb-2 space-y-2">
          <input type="text" placeholder="Nome per Stock" className="w-full border p-2 rounded-xl" value={newStock.name} onChange={(e) => setNewStock((s) => ({ ...s, name: e.target.value }))} />
          <input type="number" placeholder="Stock porzioni da 2" className="w-full border p-2 rounded-xl" value={newStock.stock2} onChange={(e) => setNewStock((s) => ({ ...s, stock2: e.target.value }))} />
          <input type="number" placeholder="Stock porzioni da 5" className="w-full border p-2 rounded-xl" value={newStock.stock5} onChange={(e) => setNewStock((s) => ({ ...s, stock5: e.target.value }))} />
          <button onClick={handleAddStock} className="w-full bg-[#D39BFF] text-white py-2 rounded-xl font-semibold">Aggiorna Stock</button>
        </div>
        <ul className="divide-y divide-gray-200 mt-2">
          {stockItems.map((item, idx) => (
            <li key={idx} className="py-2">
              <div className="flex justify-between items-center">
                <p className="font-semibold">{item.name}</p>
                <p className="text-xs text-gray-400">Stock x2: {item.stock2} | Stock x5: {item.stock5}</p>
              </div>
            </li>
          ))}
        </ul>
      </details>

      {/* Modale Dettagli Ordine */}
      <AnimatePresence>
        {openOrderModal && (
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50 p-4">
            <motion.div initial={{ scale: 0.8 }} animate={{ scale: 1 }} exit={{ scale: 0.8 }} transition={{ type: 'spring', stiffness: 300, damping: 20 }} className="bg-white p-6 rounded-2xl max-w-md w-full max-h-[95vh] overflow-y-auto relative">
              <button onClick={() => setOpenOrderModal(null)} className="absolute top-4 right-4 text-gray-400 hover:text-gray-600">✕</button>
              <div className="mb-4">
                <h2 className="text-2xl font-bold text-[#D39BFF] mb-2">Info Ordine</h2>
                <p><span className="font-semibold">Nome:</span> {openOrderModal.name}</p>
                <p><span className="font-semibold">Categoria:</span> {openOrderModal.category}</p>
                <p><span className="font-semibold">Quantità:</span> {openOrderModal.quantity}</p>
                <p><span className="font-semibold">Prezzo unitario:</span> €{openOrderModal.price}</p>
                <p><span className="font-semibold">Totale Ordine:</span> €{calculateTotal(openOrderModal)}</p>
              </div>
              <hr className="border-gray-200 my-4" />
              <div className="mb-4">
                <h2 className="text-2xl font-bold text-[#D39BFF] mb-2">Info Cliente</h2>
                <p><span className="font-semibold">Nick:</span> {openOrderModal.nick || 'Anonimo'}</p>
                <p><span className="font-semibold">Messaggio:</span> {openOrderModal.message || '-'}</p>
              </div>
              <hr className="border-gray-200 my-4" />
              <div className="mb-4">
                <h2 className="text-2xl font-bold text-[#D39BFF] mb-2">Consegna</h2>
                <p><span className="font-semibold">Indirizzo:</span> {openOrderModal.address}</p>
              </div>
              <button onClick={() => handleCloseOrder(openOrderModal)} className="w-full bg-[#D39BFF] text-white py-2 rounded-xl font-semibold">Chiudi Ordine</button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
