import { useState } from 'react';
import productData from './data.json';
import logoFyfe from './assets/logo_fyfe.png';
import cakeIcon from './assets/torta_icon.png';
import saladIcon from './assets/salad_icon.png';
import specialsIcon from './assets/special_icon.png';
import { motion, AnimatePresence } from 'framer-motion';

const categories = {
  Cake: [
    { id: 1, name: 'Red Velvet', count: 0 },
    { id: 2, name: 'Torta al Limone', count: 0 },
  ],
  Insalate: [
    { id: 3, name: 'Greca', count: 0 },
    { id: 4, name: 'Caprese', count: 0 },
  ],
  Speciali: [
    { id: 5, name: 'Paccheri', count: 0 },
    { id: 6, name: 'Risotto Tartufato', count: 0 },
  ],
};

const categoryIcons = {
  Cake: <img src={cakeIcon} alt="Cake" className="w-10 h-10 object-contain" />,
  Insalate: <img src={saladIcon} alt="Salad" className="w-10 h-10 object-contain" />,
  Speciali: <img src={specialsIcon} alt="Specials" className="w-10 h-10 object-contain" />,
};

export default function FyfeApp() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showPasswordPrompt, setShowPasswordPrompt] = useState(false);
  const [passwordInput, setPasswordInput] = useState('');
  const [passwordError, setPasswordError] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [dishes, setDishes] = useState(categories);
  const [stock, setStock] = useState(() => {
    const initialStock = {};
    for (const id in productData) {
      initialStock[id] = productData[id].stock;
    }
    return initialStock;
  });
  const [currentPage, setCurrentPage] = useState(null);
  const [showAddPanel, setShowAddPanel] = useState(false);
  const [newProduct, setNewProduct] = useState({ name: '', stock: '', price: '' });

  const updateCount = (cat, id, qty) => {
    setDishes(prev => ({
      ...prev,
      [cat]: prev[cat].map(d => (d.id === id ? { ...d, count: d.count + qty } : d)),
    }));
    setStock(prev => ({
      ...prev,
      [id]: Math.max((prev[id] || 0) - qty, 0),
    }));
  };

  const setCustomQty = (cat, id) => {
    const qty = parseInt(prompt('Quante porzioni vuoi aggiungere?'));
    if (!isNaN(qty)) updateCount(cat, id, qty);
  };

  const handleAddProduct = () => {
    const nextId = Object.values(dishes).flat().length + 1;
    const { name, stock: newStock, price } = newProduct;
    if (!name || !newStock || !price) return;
    const newDish = { id: nextId, name, count: 0 };
    setDishes(prev => ({
      ...prev,
      Speciali: [...prev.Speciali, newDish],
    }));
    setStock(prev => ({
      ...prev,
      [nextId]: parseInt(newStock),
    }));
    setNewProduct({ name: '', stock: '', price: '' });
    setShowAddPanel(false);
  };

  if (!isAuthenticated) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-white">
        {!showPasswordPrompt ? (
          <button onClick={() => setShowPasswordPrompt(true)} className="focus:outline-none">
            <motion.img
              src={logoFyfe}
              alt="Fyfe Logo"
              className="w-32 h-32 object-contain"
              animate={{ scale: [1, 1.1, 1] }}
              transition={{ duration: 1.2, repeat: Infinity }}
            />
          </button>
        ) : (
          <div className="text-center">
            <h2 className="mb-4 text-xl font-semibold">u are real?ðŸ‘‡</h2>
            <input
              type="password"
              className="border px-4 py-2 rounded-xl"
              value={passwordInput}
              onChange={e => setPasswordInput(e.target.value)}
            />
            {passwordError && <p className="text-red-500 mt-4">ðŸ¤˜ ðŸ¤˜ ðŸ¤˜ ðŸ¤˜ ðŸ¤˜ ðŸ¤˜</p>}
            <div className="mt-4">
              <button
                className="bg-[#D39BFF] text-white px-4 py-2 rounded-xl"
                onClick={() => {
                  if (passwordInput === 'fyfeadmin') {
                    setIsAuthenticated(true);
                    setIsAdmin(true);
                    setPasswordError(false);
                  } else if (passwordInput === 'fyfe2025') {
                    setIsAuthenticated(true);
                    setIsAdmin(false);
                    setPasswordError(false);
                  } else {
                    setPasswordError(true);
                  }
                }}
              >
                Entra
              </button>
            </div>
          </div>
        )}
      </div>
    );
  }

  if (isAdmin) {
    const totalOrders = Object.entries(dishes)
      .flatMap(([cat, items]) => items.map(d => ({ ...d, category: cat })))
      .filter(d => d.count > 0);

    return (
      <div className="p-4 max-w-md mx-auto bg-white min-h-screen overflow-hidden">
        <h2 className="text-2xl font-bold text-[#D39BFF] text-center mb-6">Dashboard Gestore</h2>

        <div className="space-y-4">
          <details open className="bg-white border rounded-2xl shadow p-4">
            <summary className="flex items-center gap-2 cursor-pointer">
              <span className="h-6 w-6 bg-[#D39BFF] rounded-full"></span>
              <span className="text-lg font-semibold text-[#D39BFF]">Ordini Attivi</span>
            </summary>
            {totalOrders.length === 0 ? (
              <p className="text-gray-500 mt-2">Nessun ordine attivo</p>
            ) : (
              <ul className="divide-y divide-gray-200 mt-2">
                {totalOrders.map(item => (
                  <li key={item.id} className="py-2">
                    <div className="flex justify-between items-center">
                      <div>
                        <p className="font-semibold">{item.name}</p>
                        <p className="text-xs text-gray-400">Categoria: {item.category}</p>
                      </div>
                      <div className="flex items-center gap-2">
                        <span className="font-bold text-[#D39BFF]">x{item.count}</span>
                        <button
                          className="bg-red-500 text-white text-xs px-2 py-1 rounded-xl"
                          onClick={() => {
                            setDishes(prev => ({
                              ...prev,
                              [item.category]: prev[item.category].map(d =>
                                d.id === item.id ? { ...d, count: 0 } : d
                              ),
                            }));
                          }}
                        >
                          Completato
                        </button>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </details>

          <div className="text-center">
            <button
              className="text-2xl bg-[#D39BFF] text-white rounded-full w-12 h-12"
              onClick={() => setShowAddPanel(prev => !prev)}
            >
              +
            </button>
          </div>

          <AnimatePresence>
            {showAddPanel && (
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 20 }}
                className="bg-gray-100 p-4 rounded-2xl shadow"
              >
                <p className="text-[#D39BFF] font-semibold mb-2">Inserisci prodotto</p>
                <input
                  type="text"
                  placeholder="Nome"
                  className="block w-full mb-2 px-3 py-2 rounded-xl border"
                  value={newProduct.name}
                  onChange={e => setNewProduct({ ...newProduct, name: e.target.value })}
                />
                <input
                  type="number"
                  placeholder="Stock"
                  className="block w-full mb-2 px-3 py-2 rounded-xl border"
                  value={newProduct.stock}
                  onChange={e => setNewProduct({ ...newProduct, stock: e.target.value })}
                />
                <input
                  type="number"
                  placeholder="Prezzo"
                  className="block w-full mb-4 px-3 py-2 rounded-xl border"
                  value={newProduct.price}
                  onChange={e => setNewProduct({ ...newProduct, price: e.target.value })}
                />
                <button
                  className="bg-[#D39BFF] text-white px-4 py-2 rounded-xl"
                  onClick={handleAddProduct}
                >
                  Salva Prodotto
                </button>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        <div className="text-center mt-6">
          <button
