import React, { useReducer, useMemo, lazy, Suspense } from "react";

/* -------------------------------------------------------------
   UTILITY
-------------------------------------------------------------*/
function calculateTotal(orders) {
  return orders.reduce((sum, order) => sum + order.total, 0);
}

/* -------------------------------------------------------------
   REDUCER
-------------------------------------------------------------*/
function dashboardReducer(state, action) {
  switch (action.type) {
    case "ADD_ACTIVE_ORDER":
      return { ...state, activeOrders: [...state.activeOrders, action.order] };
    case "COMPLETE_ORDER":
      return {
        ...state,
        activeOrders: state.activeOrders.filter((o) => o.id !== action.id),
        completedOrders: [...state.completedOrders, action.order],
      };
    case "ADD_STOCK_ITEM":
      return { ...state, stockItems: [...state.stockItems, action.item] };
    case "TOGGLE_ORDER_MODAL":
      return { ...state, isOrderModalOpen: !state.isOrderModalOpen };
    default:
      return state;
  }
}

const initialState = {
  activeOrders: [],
  completedOrders: [],
  stockItems: [],
  isOrderModalOpen: false,
};

/* -------------------------------------------------------------
   MAIN COMPONENT (PARENT)
-------------------------------------------------------------*/
// Lazy‑load modal (verrà creato in un file separato OrderModal.jsx)
const OrderModal = lazy(() => import("./OrderModal"));

export default function DashboardPage() {
  const [state, dispatch] = useReducer(dashboardReducer, initialState);

  const totalRevenue = useMemo(
    () => calculateTotal(state.completedOrders),
    [state.completedOrders]
  );

  return (
    <div className="flex flex-col gap-4 p-4">
      <HeaderBar totalRevenue={totalRevenue} />

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4">
        <AddProductPanel
          onAddProduct={(product) =>
            dispatch({ type: "ADD_STOCK_ITEM", item: product })
          }
        />
        <OrdersActiveList
          orders={state.activeOrders}
          onComplete={(order) =>
            dispatch({ type: "COMPLETE_ORDER", id: order.id, order })
          }
        />
        <StockSection items={state.stockItems} />
      </div>

      <CompletedOrdersList orders={state.completedOrders} />

      <ChatBox />

      <Suspense fallback={<div>Caricamento…</div>}>
        {state.isOrderModalOpen && (
          <OrderModal
            onClose={() => dispatch({ type: "TOGGLE_ORDER_MODAL" })}
          />
        )}
      </Suspense>
    </div>
  );
}

/* -------------------------------------------------------------
   CHILD COMPONENTS
-------------------------------------------------------------*/
function HeaderBar({ totalRevenue }) {
  return (
    <header className="flex items-center justify-between bg-slate-800 text-white rounded-2xl p-4 shadow">
      <h1 className="text-2xl font-bold">Dashboard</h1>
      <span className="text-lg">Revenue Totale: €{totalRevenue.toFixed(2)}</span>
    </header>
  );
}

function AddProductPanel({ onAddProduct }) {
  const [name, setName] = React.useState("");
  const [price, setPrice] = React.useState("");

  return (
    <section className="bg-white rounded-2xl shadow p-4 flex flex-col gap-2">
      <h2 className="text-xl font-semibold">Aggiungi prodotto</h2>
      <input
        className="border rounded p-2"
        placeholder="Nome"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <input
        className="border rounded p-2"
        placeholder="Prezzo"
        type="number"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
      />
      <button
        className="bg-blue-600 text-white rounded p-2"
        onClick={() => {
          if (!name || !price) return;
          onAddProduct({ id: Date.now(), name, qty: 1, price: parseFloat(price) });
          setName("");
          setPrice("");
        }}
      >
        Salva
      </button>
    </section>
  );
}

function OrdersActiveList({ orders, onComplete }) {
  return (
    <section className="bg-white rounded-2xl shadow p-4">
      <h2 className="text-xl font-semibold mb-2">Ordini Attivi</h2>
      <ul className="flex flex-col gap-2 max-h-80 overflow-y-auto">
        {orders.map((order) => (
          <li
            key={order.id}
            className="flex items-center justify-between bg-slate-100 rounded p-2"
          >
            <span>{order.name}</span>
            <button
              className="bg-green-600 text-white rounded p-1 text-sm"
              onClick={() => onComplete(order)}
            >
              Completa
            </button>
          </li>
        ))}
        {orders.length === 0 && (
          <p className="text-slate-500">Nessun ordine attivo.</p>
        )}
      </ul>
    </section>
  );
}

function CompletedOrdersList({ orders }) {
  return (
    <section className="bg-white rounded-2xl shadow p-4 mt-4">
      <h2 className="text-xl font-semibold mb-2">Ordini Completati</h2>
      <ul className="flex flex-col gap-2 max-h-64 overflow-y-auto">
        {orders.map((order) => (
          <li
            key={order.id}
            className="flex justify-between bg-slate-100 rounded p-2"
          >
            <span>{order.name}</span>
            <span className="font-semibold">€{order.total?.toFixed(2) ?? 0}</span>
          </li>
        ))}
        {orders.length === 0 && (
          <p className="text-slate-500">Nessun ordine completato.</p>
        )}
      </ul>
    </section>
  );
}

function StockSection({ items }) {
  return (
    <section className="bg-white rounded-2xl shadow p-4">
      <h2 className="text-xl font-semibold mb-2">Magazzino</h2>
      <ul className="flex flex-col gap-2 max-h-64 overflow-y-auto">
        {items.map((item) => (
          <li
            key={item.id}
            className="flex justify-between bg-slate-50 rounded p-2"
          >
            <span>{item.name}</span>
            <span className="font-semibold">{item.qty}</span>
          </li>
        ))}
        {items.length === 0 && (
          <p className="text-slate-500">Magazzino vuoto.</p>
        )}
      </ul>
    </section>
  );
}

function ChatBox() {
  const [messages, setMessages] = React.useState([]);
  const [input, setInput] = React.useState("");

  return (
    <section className="bg-white rounded-2xl shadow p-4 mt-4">
      <h2 className="text-xl font-semibold mb-2">Chat</h2>
      <div className="flex flex-col gap-2 max-h-64 overflow-y-auto mb-2">
        {messages.map((m, idx) => (
          <div key={idx} className="bg-slate-100 rounded p-2">
            {m}
          </div>
        ))}
      </div>
      <div className="flex gap-2">
        <input
          className="flex-1 border rounded p-2"
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button
          className="bg-blue-600 text-white rounded p-2"
          onClick={() => {
            if (!input) return;
            setMessages([...messages, input]);
            setInput("");
          }}
        >
          Invia
        </button>
      </div>
    </section>
  );
}
