import React, { useState, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

export default function DashboardPage({ onLogout }) {
  const [showProductPanel, setShowProductPanel] = useState(false);
  const [showStockPanel, setShowStockPanel] = useState(false);
  const [openOrderModal, setOpenOrderModal] = useState(null);

  const [activeOrders, setActiveOrders] = useState([
    { id: 1, name: 'Red Velvet', category: 'Cake', address: 'Via Roma 123, Milano', quantity: 2, price: 5 },
    { id: 2, name: 'Caprese', category: 'Insalate', address: 'Corso Italia 45, Napoli', quantity: 5, price: 8 }
  ]);
  const [completedOrders, setCompletedOrders] = useState([]);

  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', price: '', quantity: '', image: null, imageUrl: '' });

  const [stockItems, setStockItems] = useState([]);
  const [newStock, setNewStock] = useState({ name: '', stock2: '', stock5: '' });

  const hiddenFileInput = useRef(null);

  const calculateTotal = o => o.price * o.quantity;
  const totalRevenue = completedOrders.reduce((sum, o) => sum + calculateTotal(o), 0);

  const handleImageClick = () => hiddenFileInput.current?.click();
  const handleImageUpload = e => {
    const file = e.target.files[0];
    if (file) setNewProduct(p => ({ ...p, image: file, imageUrl: URL.createObjectURL(file) }));
  };

  const handleAddProduct = () => {
    const { name, price, quantity } = newProduct;
    if (!name || !price || !quantity) return alert('Compila tutti i campi per il prodotto.');
    setProducts(prev => [...prev, { ...newProduct, price: parseFloat(price), quantity: parseInt(quantity, 10) }]);
    setNewProduct({ name: '', price: '', quantity: '', image: null, imageUrl: '' });
    setShowProductPanel(false);
  };

  const handleAddStock = () => {
    const { name, stock2, stock5 } = newStock;
    if (!name || !stock2 || !stock5) return alert('Compila tutti i campi per lo stock.');
    const entry = { name, price: 0, stock2: parseInt(stock2, 10), stock5: parseInt(stock5, 10) };
    setStockItems(prev =>
      prev.some(i => i.name === name)
        ? prev.map(i => (i.name === name ? entry : i))
        : [...prev, entry]
    );
    setNewStock({ name: '', stock2: '', stock5: '' });
    setShowStockPanel(false);
  };

  const completeOrder = order => {
    setCompletedOrders(prev => [...prev, order]);
    setActiveOrders(prev => prev.filter(o => o.id !== order.id));
    setOpenOrderModal(null);
  };

  return (
    <div className="min-h-screen bg-white p-6">
      {/* HEADER */}
      <div className="bg-[#D39BFF] rounded-b-2xl px-6 py-8 text-center relative">
        <button onClick={onLogout} className="absolute left-6 top-6 text-white underline">
          ← Logout
        </button>
        <h1 className="text-4xl font-bold text-white">Dashboard</h1>
      </div>

      {/* ACTION BUTTONS */}
      <div className="flex justify-end -mt-8 mb-4 space-x-4">
        <motion.button
          onClick={() => setShowStockPanel(true)}
          className="w-14 h-14 rounded-full bg-white border-2 border-[#D39BFF] text-[#D39BFF] shadow-lg flex items-center justify-center"
          whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}
        >
          Stock
        </motion.button>
        <motion.button
          onClick={() => setShowProductPanel(true)}
          className="w-14 h-14 rounded-full bg-[#D39BFF] border-2 border-white text-white shadow-lg flex items-center justify-center"
          whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}
        >
          +
        </motion.button>
      </div>

      {/* ORDINI ATTIVI */}
      <div className="bg-white rounded-2xl shadow p-4 mb-4 max-w-md mx-auto">
        <div className="flex items-center mb-4">
          <span className="h-4 w-4 bg-[#D39BFF] rounded-full mr-2" />
          <h2 className="text-[#D39BFF] font-semibold text-xl">Ordini Attivi</h2>
        </div>
        <ul className="divide-y divide-gray-200">
          {activeOrders.map(o => (
            <li key={o.id} className="py-3 flex justify-between items-center cursor-pointer" onClick={() => setOpenOrderModal(o)}>
              <div>
                <p className="font-semibold">{o.name}</p>
                <p className="text-xs text-gray-400">Qty: {o.quantity}</p>
              </div>
              <span className="font-bold text-[#D39BFF]">€{calculateTotal(o)}</span>
            </li>
          ))}
        </ul>
      </div>

      {/* MODALE DETTAGLI ORDINE */}
      <AnimatePresence>
        {openOrderModal && (
          <motion.div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center p-4">
            <motion.div className="bg-white rounded-2xl shadow p-6 max-w-sm w-full"
              initial={{ scale: 0.8 }} animate={{ scale: 1 }} exit={{ scale: 0.8 }}>
              <h3 className="text-xl font-bold mb-2">{openOrderModal.name}</h3>
              <p className="text-sm text-gray-600 mb-1">Categoria: {openOrderModal.category}</p>
              <p className="text-sm text-gray-600 mb-4">Address: {openOrderModal.address}</p>
              <p className="mb-4">Quantità: <span className="font-semibold">{openOrderModal.quantity}</span></p>
              <div className="flex justify-end space-x-2">
                <button onClick={() => setOpenOrderModal(null)} className="px-4 py-2 rounded-xl border">Chiudi</button>
                <button onClick={() => completeOrder(openOrderModal)} className="px-4 py-2 rounded-xl bg-[#D39BFF] text-white">Completa</button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* ORDINI TOTALI */}
      <div className="bg-white rounded-2xl shadow p-4 mb-4 max-w-md mx-auto flex justify-between items-center">
        <div className="flex items-center">
          <span className="h-4 w-4 bg-gray-300 rounded-full mr-2" />
          <h2 className="text-gray-400 font-semibold text-xl">Ordini Totali</h2>
        </div>
        <p className="text-[#D39BFF] font-semibold">Incasso: €{totalRevenue.toFixed(2)}</p>
      </div>

      {/* PANEL AGGIUNGI PRODOTTO */}
      <AnimatePresence>
        {showProductPanel && (
          <motion.div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center p-4">
            <motion.div className="bg-white rounded-2xl shadow p-6 max-w-sm w-full"
              initial={{ scale: 0.8 }} animate={{ scale: 1 }} exit={{ scale: 0.8 }}>
              <h3 className="text-xl font-bold mb-4">Aggiungi Prodotto</h3>
              <input type="text" placeholder="Nome" value={newProduct.name}
                onChange={e => setNewProduct(p => ({ ...p, name: e.target.value }))}
                className="w-full border p-2 rounded-xl mb-3" />
              <input type="number" placeholder="Prezzo" value={newProduct.price}
                onChange={e => setNewProduct(p => ({ ...p, price: e.target.value }))}
                className="w-full border p-2 rounded-xl mb-3" />
              <input type="number" placeholder="Quantità" value={newProduct.quantity}
                onChange={e => setNewProduct(p => ({ ...p, quantity: e.target.value }))}
                className="w-full border p-2 rounded-xl mb-3" />
              <div className="mb-3">
                <button onClick={handleImageClick} className="w-full border border-gray-200 p-4 rounded-xl">
                  {newProduct.imageUrl
                    ? <img src={newProduct.imageUrl} alt={newProduct.name} className="object-cover w-full h-32 rounded-xl" />
                    : 'Carica immagine'}
                </button>
                <input type="file" accept="image/*" ref={hiddenFileInput} className="hidden" onChange={handleImageUpload} />
              </div>
              <div className="flex justify-end space-x-2">
                <button onClick={() => setShowProductPanel(false)} className="px-4 py-2 rounded-xl border">Annulla</button>
                <button onClick={handleAddProduct} className="px-4 py-2 rounded-xl bg-[#D39BFF] text-white">Aggiungi</button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* PANEL AGGIUNGI STOCK */}
      <AnimatePresence>
        {showStockPanel && (
          <motion.div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center p-4">
            <motion.div className="bg-white rounded-2xl shadow p-6 max-w-sm w-full"
              initial={{ scale: 0.8 }} animate={{ scale: 1 }} exit={{ scale: 0.8 }}>
              <h3 className="text-xl font-bold mb-4">Stock Prodotti</h3>
              <input type="text" placeholder="Nome per Stock" value={newStock.name}
                onChange={e => setNewStock(s => ({ ...s, name: e.target.value }))}
                className="w-full border p-2 rounded-xl mb-3" />
              <input type="number" placeholder="Stock 2pz" value={newStock.stock2}
                onChange={e => setNewStock(s => ({ ...s, stock2: e.target.value }))}
                className="w-full border p-2 rounded-xl mb-3" />
              <input type="number" placeholder="Stock 5pz" value={newStock.stock5}
                onChange={e => setNewStock(s => ({ ...s, stock5: e.target.value }))}
                className="w-full border p-2 rounded-xl mb-3" />
              <div className="flex justify-end space-x-2">
                <button onClick={() => setShowStockPanel(false)} className="px-4 py-2 rounded-xl border">Annulla</button>
                <button onClick={handleAddStock} className="px-4 py-2 rounded-xl bg-[#D39BFF] text-white">Aggiorna</button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* STOCK LIST */}
      {stockItems.length > 0 && (
        <div className="bg-white rounded-2xl shadow p-4 max-w-md mx-auto">
          <ul className="divide-y divide-gray-200">
            {stockItems.map(i => (
              <li key={i.name} className="py-2 flex justify-between">
                <p className="font-semibold text-black">{i.name}</p>
                <p className="text-sm text-gray-600">2pz: {i.stock2} | 5pz: {i.stock5}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
